import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { userInterface } from '../../../interface/interfaces';
import { Grid, Typography } from '@mui/material';
import MUIDataTable from 'mui-datatables';
import Popup from '../../Popup';
import Button from '../../Button';
import PatientEditForm from '../product/ProductEditForm';
import { USER_QUERY } from '../../../graphql/Users';
import Controls from '../../Controls';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import CloseIcon from '@material-ui/icons/Close';

export const UserList = ({title,roleId}:any) => {
    const [openPopup,setOpenPopup] =useState(false);
    const [openCofirmPopup,setOpenConfirimPopup]=useState(false);
    const [newData,setNewData]=useState("");
    const { loading, error, data } = useQuery(USER_QUERY, {
      fetchPolicy: 'cache-first',
      variables: { roleId },
    });

    if(loading) return <p>Loading...</p>
    if (error) return <p>{error.message}</p>
    console.log(data)
    const row = data.users.map((row:userInterface)=>(
        [row.id,row.firstName,row.lastName,row.username,row.email,row.role]
    ))
    const columns = [
        {
          name: "#Id",
          options: {
            filter: true,
          }
        },
        {
          label: "First Name",
          name: "Title",
          options: {
            filter: true,
          }
        },
        {
          name: "Last Name",
          options: {
            filter: true,
            sort: false,
          }
        },
        {
          name: "Username",
          options: {
            filter: true,
            sort: false,
          }
        },
        {
          name: "Email",
          options: {
            filter: true,
            sort: false,
          }
        },
        {
          name: "Role",
          options: {
            filter: true,
            sort: false,
          }
        },
        {
          name: "Edit",
          options: {
            filter: true,
            sort: false,
            empty: true,
            customBodyRender: (value: any, tableMeta: any, updateValue: any) => {
              return (
                <Controls.ActionButton
                color="primary"
                onClick={() => { setOpenPopup(true);setNewData(tableMeta.rowData);  }}
                >

                  <EditOutlinedIcon fontSize="small" />
                </Controls.ActionButton>
              );
            }
          }
        },
        {
          name: "Delete",
          options: {
            filter: true,
            sort: false,
            empty: true,
            customBodyRender: (value: any, tableMeta: any, updateValue: any) => {
              return (
                <Controls.ActionButton
                color="secondary"
                onClick={() => { setOpenConfirimPopup(true);setNewData(tableMeta.rowData);  }}
                >
                <CloseIcon fontSize="small" />
            </Controls.ActionButton>
              );
            }
          }
        }
      ];
      return (
        <Grid>
             <MUIDataTable
               title={title}
               data={row}
               columns={columns}
               options={{
                 filterType: "checkbox",
               }}
             />
   <Popup
                    title="Patient Edit Form"
                    openPopup={openCofirmPopup}
                    setOpenPopup={setOpenConfirimPopup}
                >
                  <Grid>
    <Typography>Are you sure you want to delete this post?</Typography>
    <Grid>
    <Button
     //onClick={handleDelete(newData[0])}
            text="Yes" />
    <Button
            type="submit"
            text="No" />
    </Grid>
   </Grid>
  </Popup>
  <Popup
                    title="Patient Edit Form"
                    openPopup={openPopup}
                    setOpenPopup={setOpenPopup}
                >
     <PatientEditForm id = {newData[0]}  />
         </Popup>
        </Grid>
      )
}
